.positionbtn {
  margin-right: 45px;
  margin-bottom: 30px;
  margin-top: 25px;
}
.v-card--reveal {
  align-items: center;
  bottom: 0;
  justify-content: center;
  opacity: 0.87;
  position: absolute;
  width: 100%;
}
.estilohover {
  height: 100%;
  font-size: 25px;
}
.column-6 {
  float: left;
  width: 33.33333%;
  padding: 10px;
}
.img2 {
  width: 380px !important;
  height: auto !important;
  border-radius: 10px !important;
  margin-bottom: 8px !important;
}
.img2:hover {
  transform: scale(1.02);
  -webkit-box-shadow: 0px 0px 16px 1px rgba(0, 0, 0, 0.36);
  -moz-box-shadow: 0px 0px 16px 1px rgba(0, 0, 0, 0.36);
  box-shadow: 0px 0px 16px 1px rgba(0, 0, 0, 0.36);
}
.positionbtn {
  margin-right: 45px;
  margin-bottom: 45px;
}
.widthCard {
  width: 90% !important;
}
@media screen and (max-width: 1000px) {
  .column-6 {
    width: 103%;
    text-align: center;
  }
  .img {
    margin: auto;
  }
  .img2 {
    margin: auto;
    width: 350px !important;
    height: auto !important;
  }
  .widthCard {
    width: none;
  }
}
